import { Component, OnInit } from '@angular/core';
import { DataService } from '../../../services/data.service';
import { Product } from '../../../shared/interfaces/Product';

@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.css']
})
export class ProductsComponent {
  constructor(private service: DataService) {}
  products: Product[] = [];

  ngOnInit() {
    this.service.getAllProducts().subscribe((products) => {
      console.log('the products', products.products);
      //this.products = products;
    });
  }

}
<page-title title="Products"></page-title>
<page-description pageDescription="List of all products"></page-description>

<list-table [items]="products.products"></list-table>
