import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { DataService } from '../../../services/data.service';
import { User } from '../../../shared/interfaces/User';

@Component({
  selector: 'app-user-detail',
  templateUrl: './user-detail.component.html',
  styleUrls: ['./user-detail.component.css'],
})
export class UserDetailComponent implements OnInit {
  user!: User;
  userImage: string = '';
  constructor(
    private activatedRoute: ActivatedRoute,
    private dataService: DataService,
    private router: Router
  ) {}

  ngOnInit() {
    this.activatedRoute.params.subscribe((params: any) => {
      console.log('params', params);
      if (params.id)
        this.dataService.getUserByID(params.id).subscribe((serverItem) => {
          this.user = serverItem;
          this.userImage = this.user.image;
        });
    });
  }

  // ngAfterViewInit() {
  //   console.log('the product in product-detail comp', this.user);
  //   this.userImage = this.user.image;
  // }

  fullName(first: string, maiden: string, last: string) {
    return first + ' ' + maiden + ' ' + last;
  }

  getAddress(address: any): string {
    console.log(address);
    let fullAddress =
      address.address +
      ' ' +
      address.city +
      ', ' +
      address.postalCode +
      ' ' +
      address.state +
      '.';
    return fullAddress;
  }
}
<section id="product-page">
  <div class="product-details">
    <div class="product-img">
      <div class="swiper mySwiper">
        <div class="swiper-wrapper">
          <div class="swiper-slide">
            <img [src]="userImage" />
          </div>
        </div>
      </div>
    </div>
    <div class="product-text">
      <span class="product-category">{{user.birthDate | date}}</span>
      <h3>{{fullName(user.firstName, user.maidenName, user.lastName)}}</h3>
      <span class="product-price">{{user.phone}}</span>
      <p><b>Email:</b> {{user.email}}</p>
      <p><b>Address:</b>{{getAddress(user.address)}}</p>

      <div class="product-size-container">
        <strong>User Bio:</strong>
          <p><b>BloodGroup:</b> {{user.bloodGroup}} &nbsp;</p>
          <p><b>Height:</b> {{user.height}} &nbsp;</p>
          <p><b>Weight:</b> {{user.weight}} &nbsp;</p>
          <p><b>EyeColor:</b> {{user.eyeColor}} &nbsp;</p>
          <p><b>Hair Type:</b> {{user.hair.color}} and {{user.hair.type}}</p>

      </div>
    </div>
  </div>
</section>
