import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { DataService } from '../../../services/data.service';
import { User } from '../../../shared/interfaces/User';

@Component({
  selector: 'app-user-detail',
  templateUrl: './user-detail.component.html',
  styleUrls: ['./user-detail.component.css'],
})
export class UserDetailComponent {
  user!: User;
  constructor(
    activatedRoute: ActivatedRoute,
    dataService: DataService,
    private router: Router
  ) {
    activatedRoute.params.subscribe((params: any) => {
      console.log('params', params);
      if (params.id)
        dataService.getUserByID(params.id).subscribe((serverItem) => {
          this.user = serverItem;
        });
    });
  }

  fullName(first: string, maiden: string, last: string) {
    return first + ' ' + maiden + ' ' + last;
  }
}
<page-title [title]="fullName(user.firstName, user.maidenName, user.lastName)"></page-title>

<p>{{user | json}}</p>

<section id="product-page">
  <div class="product-details">
    <div class="product-img">

      <!-- Swiper Slider -->
      <div class="swiper mySwiper">
        <div class="swiper-wrapper">
          <div class="swiper-slide">
              <img [src]="user.image" />
          </div>
        </div>

        <!--btns-->
        <div class="slider-btns">
          <button class="swiper-button-prev"></button>
          <button class="swiper-button-next"></button>
        </div>
      </div>

    </div>
    <div class="product-text">
      <span class="product-category">{{user.birthDate}}</span>
      <h3>{{fullName(user.firstName, user.maidenName, user.lastName)}}</h3>
      <span class="product-price">{{product.price | currency}}</span>
      <p>{{product.description}}</p>
      <!--size-->
      <div class="product-size-container">
        <strong>Select Size:-</strong>
        <div class="product-size">
          <!--xl-->
          <input type="checkbox" class="s-checkbox" id="s-xl">
          <label for="s-xl" class="s-label">XL</label>
          <!--Small-->
          <input type="checkbox" class="s-checkbox" id="s-s">
          <label for="s-s" class="s-label">S</label>
          <!--Medium-->
          <input type="checkbox" class="s-checkbox" id="s-m">
          <label for="s-m" class="s-label">M</label>
          <!--Large-->
          <input type="checkbox" class="s-checkbox" id="s-l">
          <label for="s-l" class="s-label">L</label>
        </div>
      </div>

      <!--btn-->
      <div class="product-button">
        <a href="#" class="add-bag-btn">Add To Bag</a>
        <a href="#" class="add-wishlist-btn">Add To Wishlist</a>
      </div>
    </div>
  </div>
</section>
