import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { DataService } from '../../../services/data.service';
import { Product } from '../../../shared/interfaces/Product';

@Component({
  selector: 'app-product-detail',
  templateUrl: './product-detail.component.html',
  styleUrls: ['./product-detail.component.css']
})
export class ProductDetailComponent {
  product!: Product;
  constructor(
    activatedRoute: ActivatedRoute,
    dataService: DataService,
    private router: Router
  ) {
    activatedRoute.params.subscribe((params: any) => {
      console.log('params', params);
      if (params.id)
        dataService.getProductByID(params.id).subscribe((serverItem) => {
          this.product = serverItem;
        });
    });
  }

}
<page-title [title]="product.title"></page-title>

<p>{{product | json}}</p>

<div class="flex flex-wrap -m-4">
    <div class="xl:w-1/3 md:w-1/2 p-4">
      <div class="bg-white p-4 rounded-sm h-full">
        <img [src]="product.images[0]"
          class="lg:h-60 xl:h-56 md:h-64 sm:h-72 xs:h-72 h-72  rounded w-full object-cover object-center mb-6"
          alt="">
        <div class="flex justify-between">
          <h3 class="tracking-widest text-indigo-500 text-xs font-medium title-font">{{product.title}}</h3>
          <h4 style="text-overflow: ellipsis;
          display: -webkit-box;" class="tracking-widest text-gray-800 text-sm">{{product.price | currency}}</h4>
        </div>
        <h2 class="text-lg text-gray-900 font-medium title-font mb-4 overflow-hidden">{{article.title}}</h2>
        <p class="leading-relaxed text-base">{{article.description}}</p>
        <span><a class="text-blue-500" href="{{article.url}}" target="_blank">read more....</a></span>
      </div>
    </div>
</div>
