import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { DataService } from '../../../services/data.service';
import { Product } from '../../../shared/interfaces/Product';

@Component({
  selector: 'app-product-detail',
  templateUrl: './product-detail.component.html',
  styleUrls: ['./product-detail.component.css'],
})
export class ProductDetailComponent implements OnInit {
  product: Product;
  index: number = 0;
  imageToShow: string = '';
  numberOfImages: number = 0;

  constructor(
    private activatedRoute: ActivatedRoute,
    private dataService: DataService,
    private router: Router
  ) {}

  ngOnInit() {
    this.activatedRoute.params.subscribe((params: any) => {
      if (params.id)
        this.dataService
          .getProductByID(params.id)
          .subscribe((serverItem: Product) => {
            this.product = serverItem;
            console.log("the product is",this.product);
            this.imageToShow = this.product.images[0];
            this.numberOfImages = this.product.images.length
          });
    });
  }

  salePrice(): number {
    let salePrice = this.product.price - (this.product.price * this.product.discountPercentage/100);
    return salePrice;
  }

  prevImage() {
    if (this.index !== 0) this.index--;
    this.imageToShow = this.product.images[this.index];
  }

  nextImage() {
    if (this.index < this.product.images.length - 1) this.index++;
    this.imageToShow = this.product.images[this.index];
  }
}
<section id="product-page">
  <div class="product-details">
    <div class="product-img">
      <div class="swiper mySwiper">
        <div class="swiper-wrapper">
          <div class="swiper-slide">
            <img class="slider-img" [src]="imageToShow" />
          </div>
        </div>

        <div class="slider-btns">
          <button class="swiper-button-prev" [disabled]="index === 0" (click)="prevImage()">&lt;</button>
          <button class="swiper-button-next" [disabled]="index === numberOfImages - 1"
            (click)="nextImage()">&gt;</button>
        </div>
      </div>
    </div>
    <div class="product-text">
      <span class="product-category">{{product.brand}}</span>
      <h3>{{product.title}}</h3>
      <span class="product-price">{{product.price | currency}} | {{ product.rating }}</span><span
        class="product-rating">★</span>
      <p>{{product.description}}</p>

      <div class="product-description">
        <strong>Description:-</strong>
        <p><b>Category:</b> {{product.category}}</p>
        <p><b>In Stock:</b> {{product.stock}}</p>
        <p><b>Sale Price:</b> {{salePrice()}}</p>
      </div>

      <div class="product-button">
        <a href="javascript:;" class="add-bag-btn">Add To Bag</a>
        <a href="javascript:;" class="add-wishlist-btn">Add To Wishlist</a>
      </div>
    </div>
  </div>
</section>
